	def die(self):
		global RESTART
		if self.side=="left":
			hero.left_alives.remove(self)
			if len(hero.left_alives)==0:
				RESTART=True
		else:
			hero.right_alives.remove(self)
			if len(hero.right_alives)==0:
				RESTART=True
